import React, {useState,useEffect} from "react";
import {getCoinDetail, getCoins} from "../../api";


const useCoin = () => {
    const [loading ,setLoading] = useState(true);
    const [coin ,setCoin] = useState(null);

    const getCoin = async () => {
        const {
            match: {
                params: { id }
            }
        } = this.props;

        try {
            const { data: coin } = await getCoinDetail(id);
            setCoin(coin);
        } catch (e) {
            console.log(e);
        } finally {
            setLoading(false );
        }
    };
    useEffect(()=>{
        getCoin();
    },[]);

};

const Coin = () => {
    return "";
};

export default Coin;
