import React, {useState,useEffect} from "react";
import {getCoins} from "../../api";

const useCoins = () => {
    const [loading, setLoading] = useState(true);
    const [coins, setCoins] = useState([]);

    callCoins = async () => {
        try {
            const { data: coins } = await getCoins();
            setCoins(coins);
        } catch (e) {
            console.log(e);
        } finally {
            setLoading(false);
        }
    };
    useEffect(()=>{
        callCoins();
    },[]);
};

const Coins = ()=>{

    return "";
};

export default Coins;
